export default defineComponent({
  name: "MainLayout",
  components: {
    EssentialLink,
  },
  setup() {
    const route = useRoute();
    const router = useRouter();
    const leftDrawerOpen = ref(false);
    const miniState = ref(false);
    const searchText = ref("");
    const $q = useQuasar();
    const logout = () => {
      localStorage.clear();
      router.push("/login");
    };

    const userName = ref("");
    const userBalance = ref("");
    const isBalanceVisible = ref(false);

    const getUserData = () => {
      authServices
        .getUserData()
        .then((res) => {
          userName.value = res.data?.data?.full_name || "";
          userBalance.value = res.data?.data?.balance || "";

          $q.loading.hide();
        })
        .catch((error) => {
          $q.loading.hide();
          $q.notify({
            badgeStyle: "display:none",
            classes: "custom-Notify",
            textColor: "black-1",
            icon: "img:/images/Error.png",
            position: "bottom-right",
            message: error.response?.data?.result || "An error occurred.",
          });
        });
    };

    const pageTitel = computed(() => route.meta.pageTitel);
    const cramp = computed(() => route.meta.subTitle);
    const pageLink = computed(() => decodeURIComponent(route.fullPath));
    const titleOfSubTitel = computed(() => route.meta.titleOfSubTitel);

    const parentLink = computed(() =>
      route.matched.length > 1 ? route.matched[1].path : "",
    );

    const subTitleChildLink = computed(() => {
      const pathParts = route.path.split("/");
      const index = pathParts.indexOf(route.params.id);
      return index !== -1
        ? pathParts.slice(0, index - 1).join("/")
        : pathParts.slice(0, -1).join("/") || "/";
    });

    onMounted(() => {
      getUserData();
    });

    return {
      essentialLinks: linksList,
      leftDrawerOpen,
      miniState,
      searchText,
      pageTitel,
      userName,
      userBalance,
      isBalanceVisible,

      pageLink,
      cramp,
      parentLink,
      titleOfSubTitel,
      subTitleChildLink,
      drawerWidth: 265,
      logout,
      toggleLeftDrawer() {
        leftDrawerOpen.value = !leftDrawerOpen.value;
      },
    };
  },
});
</script>
